// ❌ Código que requiere muchos comentarios para entenderse
class InventoryManager {
  // Check if product is available
  check(p: Product, q: number): boolean {
    // Get current stock level
    const s = this.getStock(p.id);

    // Check if we have enough inventory
    // Also need to account for reserved items
    const r = this.getReserved(p.id);

    // Available = stock - reserved
    const a = s - r;

    // Return true if we have enough
    return a >= q;
  }

  // Update the inventory when order is placed
  update(pid: number, qty: number) {
    // Get current values
    const current = this.inventory[pid];

    // Subtract the quantity
    current.stock -= qty;

    // Add to reserved
    current.reserved += qty;

    // Update timestamp
    current.lastUpdated = Date.now();
  }
}

