class InventoryManager {
  isProductAvailable(product: Product, requestedQuantity: number): boolean { // [!code ++]
    const currentStock = this.getCurrentStock(product.id); // [!code ++]
    const reservedQuantity = this.getReservedQuantity(product.id); // [!code ++]
    const availableQuantity = currentStock - reservedQuantity; // [!code ++]

    return availableQuantity >= requestedQuantity; // [!code ++]
  } // [!code ++]

  reserveProductForOrder(productId: number, quantity: number): void { // [!code ++]
    const inventoryRecord = this.getInventoryRecord(productId); // [!code ++]

    inventoryRecord.decreaseStock(quantity); // [!code ++]
    inventoryRecord.increaseReserved(quantity); // [!code ++]
    inventoryRecord.updateTimestamp(); // [!code ++]
  } // [!code ++]

  private getCurrentStock(productId: number): number { // [!code ++]
    return this.inventory[productId]?.stock ?? 0; // [!code ++]
  } // [!code ++]

  private getReservedQuantity(productId: number): number { // [!code ++]
    return this.inventory[productId]?.reserved ?? 0; // [!code ++]
  } // [!code ++]

