// ❌ Problema N+1: una consulta por cada post
class PostService {
  async getPostsWithAuthors(): Promise<PostWithAuthor[]> {
    const posts = await db.posts.findMany({
      where: { published: true },
      orderBy: { createdAt: 'desc' }
    });

    // ¡Problema N+1! Una consulta por cada post
    const postsWithAuthors = [];
    for (const post of posts) {
      const author = await db.users.findUnique({
        where: { id: post.authorId },
        select: { id: true, name: true, email: true }
      });

      postsWithAuthors.push({
        ...post,
        author
      });
    }

    return postsWithAuthors;
  }

  async getPostsWithComments(): Promise<PostWithComments[]> {
    const posts = await db.posts.findMany();

    // Otra vez N+1 para comentarios
    for (const post of posts) {
      post.comments = await db.comments.findMany({
        where: { postId: post.id }
      });
    }

    return posts;
  }
}

