// ❌ Sin caché - consultas repetidas costosas
class ProductService {
  async getProduct(id: number): Promise<Product> {
    // Siempre va a la base de datos
    const product = await db.products.findUnique({
      where: { id },
      include: {
        category: true,
        reviews: {
          include: { user: true }
        },
        variants: true
      }
    });

    if (!product) {
      throw new Error('Product not found');
    }

    // Cálculo costoso que se repite
    const averageRating = product.reviews.reduce((sum, review) => 
      sum + review.rating, 0) / product.reviews.length;

    const totalStock = product.variants.reduce((sum, variant) => 
      sum + variant.stock, 0);

    return {
      ...product,
      averageRating,
      totalStock,
      isAvailable: totalStock > 0
    };
  }

  async getPopularProducts(): Promise<Product[]> {
    // Consulta pesada sin caché
    return db.products.findMany({
      where: { featured: true },
      include: { reviews: true, category: true },
      orderBy: { viewCount: 'desc' },
      take: 10
    });
  }
}
